"<?php the_permalink(); ?>" rel="bookmark">								<div class="calendar-date">									<time datetime="<?php echo date('Y-m-d', $startdate); ?>" class="icon">									  <em><?php echo $day; ?></em>									  <strong><?php echo date('F', $startdate); ?> <?php echo date("Y", $startdate); ?></strong>									  <span><?php echo date('j', $startdate); ?></span>									</time>								</div>								<h2><a href="<?php echo $postlink; ?>"><?php echo the_title(); ?></a></h2>								<h3><?php echo date('M d, Y', $startdate); ?><?php if ($enddate) { echo ' - ' . date('M d, Y', $enddate); } ?></h3>								<p><?php echo $location; ?>, TN</p>							</a>						</li>						<?php } ?>						<?php endwhile; endif; ?>						</ul>						<?php //// LANDING PAGE SECTION - NO SEARCH						} else {						// query events						$paged = get_query_var( 'paged' );						$posts = get_posts(array(							'posts_per_page'	=> 30,							'post_type'			=> 'events',							'meta_query' 		=> array(								array(									'key'			=> 'end_date',									'compare'		=> 'BETWEEN',									'value'			=> array( $date_now, $date_next_week ),									'type'			=> 'DATETIME'								)							),							'order'				=> 'ASC',							'orderby'			=> 'meta_value',							'meta_key'			=> 'start_date',							'paged'          => $paged,							'meta_type'			=> 'DATETIME'						));						if( $posts ): ?>							<ul id="events">								<?php foreach( $posts as $p ): 								$startdate = strtotime(get_field('start_date', $p->ID));								$stingdate = get_field('start_date', $p->ID);								$month = date('F', $startdate);								$location = get_field('location', $p->ID);								$enddate = get_field('end_date', $p->ID);								$day = date("l", strtotime($stingdate));									$postlink = get_permalink($p->ID);								if ($enddate) {									$enddate = strtotime(get_field('end_date', $p->ID));								}								?>									<li>									<?php if (isset($_GET['preview'])) { ?>										<div class="mod-calendar-date">											<time datetime="<?php echo date('Y-m-d', $startdate); ?>" class="icon">											  <em><?php echo $day; ?></em>											  <strong><?php echo strtoupper(date('M', $startdate)); ?> <?php echo date("Y", $startdate); ?></strong>											  <span><?php echo date('j', $startdate); ?></span>											</time>										</div>									<?php } else { ?>										<div class="calendar-date">											<time datetime="<?php echo date('Y-m-d', $startdate); ?>" class="icon">											  <em><?php echo $day; ?></em>											  <strong><?php echo date('F', $startdate); ?> <?php echo date("Y", $startdate); ?></strong>											  <span><?php echo date('j', $startdate); ?></span>											</time>										</div>									<?php } ?>											<h2><a href="<?php echo $postlink; ?>"><?php echo $p->post_title; ?></a></h2>											<h3><?php echo date('M d, Y', $startdate); ?><?php if ($enddate) { echo ' - ' . date('M d, Y', $enddate); } ?></h3>											<p><?php echo $location; ?>, TN</p>									</li>								<?php endforeach; ?>							</ul>						<?php endif; ?>					</ul>				<?php } ?>					<div class="nav-links">						<?php if (function_exists("pagination")) { pagination($wp_query->max_num_pages); } ?>					</div><!--nav-links-->					<div class="paginate-links">						<?php  echo paginate_links(); ?>					</div><!--paginate-links-->				</div><!--home-widget-->			</div><!--home-main-->		</div><!--content-main-->		<div id="sidebar-wrapper">			<?php dynamic_sidebar( 'events-sidebar' ); ?>		</div>	</div><!--content-wrapper--></div><!--main-wrapper--><?php get_footer(); ?>