<?php/*Template Name: Event Data for Copy Paste*/?><?php get_header(); ?>	<div id="content-wrapper" style="      width: 100vw;      position: relative;      margin-left: -50vw;      left: 50%;">		<?php if (have_posts()) : while (have_posts()) : the_post(); ?>		<div id="content-main" style="width:100%;">			<div id="home-main" style="padding:20px; margin-bottom:50px; width:100%;">				<div id="post-area">					<h1 class="story-title"><?php the_title(); ?></h1>					<div id="content-area"><?php the_content(); ?><?php// find date time nowwp_reset_query();$date_now = date('Y-m-d H:i:s');$time_now = strtotime($date_now);$time_one_year = strtotime('+365 days', $time_now);$date_one_year = date('Y-m-d H:i:s', $time_one_year);$url = 'http://' . $_SERVER['SERVER_NAME'] . $_SERVER['REQUEST_URI'];						// query events						$posts = get_posts(array(							'posts_per_page'	=> -1,							'post_type'			=> 'events',							'meta_query' 		=> array(								array(									'key'			=> 'end_date',									'compare'		=> 'BETWEEN',									'value'			=> array( $date_now, $date_one_year ),									'type'			=> 'DATETIME'								)							),							'order'				=> 'ASC',							'orderby'			=> 'meta_value',							'meta_key'			=> 'start_date',							'meta_type'			=> 'DATETIME'						));						if( $posts ): ?>							<table id="events" style="width:100%;">								<?php foreach( $posts as $p ): // -Start Date, -End Date, -Location, -Regions, -Title, -Content, -Phone Number, -Event URL, ID								$startdate = strtotime(get_field('start_date', $p->ID));								$month = date('F', $startdate);								$location = get_field('location', $p->ID);								$enddate = get_field('end_date', $p->ID);								$day = date("l", strtotime($startdate));									$postlink = get_permalink($p->ID);								if ($enddate) {									$enddate = strtotime(get_field('end_date', $p->ID));								}								$regions = '';								$eventcontent = get_the_content($p->ID);								$eventcontent = get_post($p->ID);								$eventcontent = $eventcontent->post_content;								$phone = get_field('phone_number', $p->ID);								$link = get_field('event_url', $p->ID);								if (!empty($link) && strpos($link, 'http') == 0) {									$link = str_replace("http://","","$link");								}								if (!empty($link) && strpos($link, 'https') == 0) {									$link = str_replace("https://","","$link");								}								$region = get_the_terms( $p->ID, 'region' );								?>									<tr>										<td>											<?php echo date('M d, Y', $startdate); ?>										</td>										<td>											<?php if ($enddate) { echo date('M d, Y', $enddate); } ?>										</td>										<td>											<?php echo $p->post_title; ?>										</td>										<td>											<?php echo $location; ?>										</td>										<td>											<?php echo $content; ?>										</td>										<td>											<?php echo $phone; ?>										</td>										<td>											<?php echo $link; ?>										</td>										<td>											<?php echo $region[0]->name; ?>										</td>										<td>											<?php echo $region[1]->name; ?>										</td>										<td>											<?php echo $eventcontent; ?>										</td>																				</tr>								<?php endforeach; ?>							</table>						<?php endif; ?>					</ul>					</div><!--content-area-->				</div><!--post-area-->			</div><!--home-main-->		</div><!--content-main-->		<?php // get_sidebar(); ?>		<?php endwhile; endif; ?>	</div><!--content-wrapper--></div><!--main-wrapper--><?php get_footer(); ?>